batchProperties.batchScript:
var data = $gc('data') || {i: 0}

if (data == null || !data.n) {
	data = {i: 0, n: parseInt(String(reader.readLine())), start: new Date()}
}
if (data.i > data.n) {
	data.end = new Date()
	data.n = undefined
	data.dt = data.end - data.start
	$gc('data', data)
	$gc('result', JSON.stringify(data, null, 2))
	return null
}
++data.i
$gc('data', data)
return data.i

